mapper.select:
SELECT d_id, d_name, d_dam, d_phone
FROM dept
WHERE d_id = #{d_id}

mapper.update:
UPDATE dept
SET d_name  = #{d_name},
    d_dam   = #{d_dam},
    d_phone = #{d_phone}
WHERE d_id = #{d_id}

mapper.select:
SELECT p_id, p_name, p_iprice, p_sprice, d_id, a_id, j_id ,p_seq
FROM product
WHERE p_seq=#{p_seq}

mapper.update:
UPDATE product
SET p_del=1
WHERE p_id=#{p_id}

UPDATE STOCK 
SET ST_NUM = NVL(ST_NUM , 0) + #{in_num} 
WHERE P_SEQ = (SELECT P_SEQ FROM PRODUCT WHERE P_NAME =  #{p_name} and J_ID = 12010 )

UPDATE STOCK 
SET ST_NUM =  NVL(ST_NUM,0) - #{dis_num} 
WHERE P_SEQ = (SELECT P_SEQ FROM PRODUCT WHERE P_NAME =  #{p_name} and J_ID = 12010)
