mapper.update:
UPDATE STOCK 
SET ST_NUM = NVL(ST_NUM , 0) + #{in_num} 
WHERE P_SEQ = (SELECT P_SEQ FROM PRODUCT WHERE P_NAME =  #{p_name} and J_ID = 12020 )

UPDATE STOCK 
SET ST_NUM =  NVL(ST_NUM,0) - #{dis_num} 
WHERE P_SEQ = (SELECT P_SEQ FROM PRODUCT WHERE P_NAME =  #{p_name} and J_ID = 12020)
