mapper.update:
   
	   	UPDATE  BOARD 
	   	  SET   STEP    = STEP + 1
	   	  WHERE M_ID = #{ m_id } 
	   	   AND  NREF    = #{ nref }
	   	   AND  STEP    > #{ step }

mapper.insert:
   		INSERT INTO  BOARD (
   			  IDX
			, M_ID
			, TITLE
			, CONT
			, E_ID
			, REGDATE
			, READCOUNT
			, BNUM
			, LVL
			, STEP
			, NREF
			, DEL
			, PNUM
   		)
   		VALUES (
   		 	  (SELECT NVL(MAX(IDX), 0) + 1 FROM BOARD)
			, #{ m_id }
			, #{ title }
			, #{ cont }
			, #{ e_id }
			, SYSDATE
			, 0
			, #{ bnum }
			, #{ lvl  } + 1
			, #{ step } + 1
			, #{ nref }
			, 0
			, #{ pnum }
   		)

insert.foreach:
INTO FILES VALUES (
	  GET_F_NUM()
	, ( SELECT MAX(IDX) FROM BOARD )
	, #{ file.f_name  }
	, #{ file.f_ext   }
	, #{ file.f_sname }
)

INTO FILES VALUES (
	  GET_F_NUM()
	, #{ idx }
	, #{ file.f_name  }
	, #{ file.f_ext   }
	, #{ file.f_sname }
)

mapper.delete:

		UPDATE  BOARD
		  SET   DEL = 1
		 WHERE IDX = #{ idx }

mapper.update:
   		
   		UPDATE  BOARD
   		   SET  TITLE = #{ title },
				CONT  = #{ cont }
   		 WHERE  IDX   = #{ idx }
